CREATE TABLE IF NOT EXISTS  MST_USER( user_id serial PRIMARY KEY, username VARCHAR(20) NOT NULL, password VARCHAR(100) NOT NULL, firstname VARCHAR(50) NOT NULL, lastname VARCHAR(50), address VARCHAR(255), phoneno character varying(20));CREATE INDEX mst_user_01 on mst_user (username);CREATE TABLE IF NOT EXISTS MST_ROLE(role_id serial PRIMARY KEY,role_name VARCHAR(30));INSERT INTO MST_ROLE(role_name)values('Customer');CREATE TABLE IF NOT EXISTS user_roles(user_id bigint,role_id bigint,PRIMARY KEY (user_id,role_id),  FOREIGN KEY (user_id) REFERENCES MST_USER(user_id),    FOREIGN KEY (role_id) REFERENCES MST_ROLE(role_id));